CREATE VIEW [Agresso_RawTyped].[vrt_DimRelationer_01]ASSELECT [SysFileName] ,[SysDatetimeInsertedUTC] ,[SysDatetimeUpdatedUTC] ,[SysDatetimeDeletedUTC] ,[SysSrcGenerationDateTime] ,[SysModifiedUTC] ,[SysExecutionLog_key] ,[Recno] ,[Section] ,[Tab] ,[Relatedattr] ,[Relvalue] ,[Attname] ,[Attvalue] ,[Client]  FROM    (SELECT * , _isFirst = LAG(0,1,1) OVER (PARTITION BY [Relatedattr] , [Attvalue] , [Client]  ORDER BY [SysSrcGenerationDateTime] DESC)  FROM [Agresso_RawTyped].[rt_DimRelationer_01]) t WHERE t._isFirst = 1