CREATE VIEW [Agresso_RawTyped].[vrt_Dimension_01]ASSELECT [SysFileName] ,[SysDatetimeInsertedUTC] ,[SysDatetimeUpdatedUTC] ,[SysDatetimeDeletedUTC] ,[SysSrcGenerationDateTime] ,[SysModifiedUTC] ,[SysExecutionLog_key] ,[Recno] ,[Section] ,[Tab] ,[Attributeid] ,[Attname] ,[DimValue] ,[DimDescription] ,[Lastupdate] ,[Periodfrom] ,[Periodto] ,[Relvalue] ,[Status] ,[Userid] ,[Value1] ,[Client]  FROM    (SELECT * , _isFirst = LAG(0,1,1) OVER (PARTITION BY [Attributeid] , [DimValue] , [Client]  ORDER BY [SysSrcGenerationDateTime] DESC)  FROM [Agresso_RawTyped].[rt_Dimension_01]) t WHERE t._isFirst = 1