CREATE VIEW [Agresso_RawTyped].[vrt_Kontoplan_01]ASSELECT [SysFileName] ,[SysDatetimeInsertedUTC] ,[SysDatetimeUpdatedUTC] ,[SysDatetimeDeletedUTC] ,[SysSrcGenerationDateTime] ,[SysModifiedUTC] ,[SysExecutionLog_key] ,[Recno] ,[Section] ,[Tab] ,[Account] ,[Description] ,[Account_grp] ,[Xaccount_grp] ,[Account_type] ,[Status] ,[UserId] ,[Lastupdate] ,[Client]  FROM    (SELECT * , _isFirst = LAG(0,1,1) OVER (PARTITION BY [Account] , [Client]  ORDER BY [SysSrcGenerationDateTime] DESC)  FROM [Agresso_RawTyped].[rt_Kontoplan_01]) t WHERE t._isFirst = 1