CREATE VIEW Norm.vn_AgreementService AS
							WITH temp AS (SELECT *, _isLast = LEAD(0,1,1) OVER (PARTITION BY AgreementService_bkey ORDER BY SysValidFromDateTime) FROM   Norm.n_AgreementService)
							SELECT  t1.AgreementService_key, t2.SysExecutionLog_key, t2.SysDatetimeInsertedUTC, t2.SysDatetimeUpdatedUTC, t2.SysDatetimeDeletedUTC, t2.SysDatetimeReprocessedUTC, t2.SysModifiedUTC, t2.SysIsInferred, t2.SysValidFromDateTime, t2.SysSrcGenerationDateTime, t2.AgreementService_bkey, t2.Agreement_bkey, t2.Service_bkey, t2.ServiceStatus_bkey, t2.Unit_bkey, t2.Site_bkey, t2.Connection_bkey, t2.FirstInvoiceDate, t2.InstallationReadyDate, t2.LastInvoiceDate, t2.TerminationNoticeDate, t2.ReplaceNoticeDate, t2.Currency, t2.MRC, t2.NRC, t2.Quantity, t2.IsReneg, t1._isLast
							FROM temp t1 
							INNER JOIN temp t2 ON t2.AgreementService_bkey = t1.AgreementService_bkey AND t2._isLast = 1